tinymce.PluginManager.add('ccake_filemanager',function(editor, url){
    window.ccake_editor = editor;
    // Adds a menu item to the tools menu
    editor.addMenuItem('ccake_filemanager', {
        text: 'Insert File',
        context: 'tools',
        onclick: function() {
            // Open window with a specific url
            var popup = editor.windowManager.open({
                title: 'Insert File',
                url: CCAKE_WWW+'adm/ccake_file_manager/tinymce',
                width: 800,
                height: 600,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
            window.ccake_popup = popup;
        }
    });
});